package main

import (
	"fmt"
	"net/http"
	"strings"
	"time"
)

// sendMessageWithRetry attempts to send a message to a specific URL within a distributed system.
// It ensures resilience in a potentially faulty environment by using retries with exponential backoff.
func sendMessageWithRetry(url string, message string, maxRetries int) error {
	const baseDelay = 2 * time.Second
	var attempt int
	for attempt = 0; attempt < maxRetries; attempt++ {
		resp, err := http.Post(url, "application/json", strings.NewReader(message))
		if err == nil && resp.StatusCode == http.StatusOK {
			resp.Body.Close()
			fmt.Printf("Message successfully sent on attempt %d\n", attempt+1)
			return nil
		}

		if resp != nil {
			resp.Body.Close()
			fmt.Printf("Attempt %d failed with status code: %d\n", attempt+1, resp.StatusCode)
		} else if err != nil {
			fmt.Printf("Attempt %d failed with error: %v\n", attempt+1, err)
		}

		time.Sleep(baseDelay * time.Duration(1<<attempt)) // Exponential backoff
	}
	return fmt.Errorf("after %d attempts, failed to send message to %s", maxRetries, url)
}

package main

import (
	"net/http"
	"net/http/httptest"
	"strings"
	"testing"
	"time"
)

func TestSendMessageWithRetry(t *testing.T) {
	// Test 1: Successful first attempt
	t.Run("SuccessfulFirstAttempt", func(t *testing.T) {
		server := httptest.NewServer(http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
			w.WriteHeader(http.StatusOK)
		}))
		defer server.Close()

		err := sendMessageWithRetry(server.URL, "{\"test\":\"message\"}", 3)
		if err != nil {
			t.Errorf("Expected success, got error: %v", err)
		}
	})

	// Test 2: Success after multiple attempts
	t.Run("SuccessAfterRetry", func(t *testing.T) {
		attempts := 0
		server := httptest.NewServer(http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
			if attempts < 2 {
				w.WriteHeader(http.StatusInternalServerError)
				attempts++
			} else {
				w.WriteHeader(http.StatusOK)
			}
		}))
		defer server.Close()

		err := sendMessageWithRetry(server.URL, "{\"test\":\"message\"}", 3)
		if err != nil {
			t.Errorf("Expected success after retry, got error: %v", err)
		}
	})

	// Test 3: Invalid URL
	t.Run("InvalidURL", func(t *testing.T) {
		err := sendMessageWithRetry("http://invalid.url.that.does.not.exist", "{\"test\":\"message\"}", 2)
		if err == nil {
			t.Error("Expected error for invalid URL, got nil")
		}
	})

	// Test 4: Max retries exceeded
	t.Run("MaxRetriesExceeded", func(t *testing.T) {
		server := httptest.NewServer(http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
			w.WriteHeader(http.StatusInternalServerError)
		}))
		defer server.Close()

		err := sendMessageWithRetry(server.URL, "{\"test\":\"message\"}", 3)
		if err == nil {
			t.Error("Expected error when max retries exceeded, got nil")
		}
	})

	// Test 5: Content validation
	t.Run("ContentValidation", func(t *testing.T) {
		var receivedContent string
		server := httptest.NewServer(http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
			buf := new(strings.Builder)
			_, _ = buf.ReadFrom(r.Body)
			receivedContent = buf.String()
			w.WriteHeader(http.StatusOK)
		}))
		defer server.Close()

		testMessage := "{\"test\":\"specific message\"}"
		err := sendMessageWithRetry(server.URL, testMessage, 3)
		if err != nil {
			t.Errorf("Expected success, got error: %v", err)
		}
		if receivedContent != testMessage {
			t.Errorf("Expected message '%s', got '%s'", testMessage, receivedContent)
		}
	})
}

