package main

import (
	"fmt"
	"crypto/aes"
	"crypto/cipher"
	"crypto/rand"
	"encoding/base64"
	"io"
)

type SecureGroup struct {
	groupKey []byte
}

func NewSecureGroup(key []byte) *SecureGroup {
	return &SecureGroup{groupKey: key}
}

func (sg *SecureGroup) EncryptMessage(message string) (string, error) {
	block, err := aes.NewCipher(sg.groupKey)
	if err != nil {
		return "", err
	}
	aesGCM, err := cipher.NewGCM(block)
	if err != nil {
		return "", err
	}
	nonce := make([]byte, aesGCM.NonceSize())
	if _, err = io.ReadFull(rand.Reader, nonce); err != nil {
		return "", err
	}
	cipherText := aesGCM.Seal(nil, nonce, []byte(message), nil)
	return base64.StdEncoding.EncodeToString(append(nonce, cipherText...)), nil
}

func (sg *SecureGroup) DecryptMessage(encodedMessage string) (string, error) {
	data, err := base64.StdEncoding.DecodeString(encodedMessage)
	if err != nil {
		return "", err
	}
	block, err := aes.NewCipher(sg.groupKey)
	if err != nil {
		return "", err
	}
	aesGCM, err := cipher.NewGCM(block)
	if err != nil {
		return "", err
	}
	nonce, cipherText := data[:aesGCM.NonceSize()], data[aesGCM.NonceSize():]
	plainText, err := aesGCM.Open(nil, nonce, cipherText, nil)
	if err != nil {
		return "", err
	}
	return string(plainText), nil
}

func main() {
	// Example usage
	group := NewSecureGroup([]byte("your-256-bit-secret-key-here"))
	encrypted, err := group.EncryptMessage("Hello, world!")
	if err != nil {
		fmt.Println("Encryption error:", err)
		return
	}
	fmt.Println("Encrypted:", encrypted)
	decrypted, err := group.DecryptMessage(encrypted)
	if err != nil {
		fmt.Println("Decryption error:", err)
		return
	}
	fmt.Println("Decrypted:", decrypted)
}

