	t.Run("Empty Message", func(t *testing.T) {
		key := make([]byte, 32)
		group := NewSecureGroup(key)
		message := ""

		encrypted, err := group.EncryptMessage(message)
		if err != nil {
			t.Fatalf("Empty message encryption failed: %v", err)
		}

		decrypted, err := group.DecryptMessage(encrypted)
		if err != nil {
			t.Fatalf("Empty message decryption failed: %v", err)
		}

		if decrypted != message {
			t.Errorf("Decrypted empty message does not match. Got %q, want %q", decrypted, message)
		}
	})

	// Test 4: Long message encryption/decryption
	t.Run("Long Message", func(t *testing.T) {
		key := make([]byte, 32)
		group := NewSecureGroup(key)
		message := strings.Repeat("Long message test. ", 1000)

		encrypted, err := group.EncryptMessage(message)
		if err != nil {
			t.Fatalf("Long message encryption failed: %v", err)
		}

		decrypted, err := group.DecryptMessage(encrypted)
		if err != nil {
			t.Fatalf("Long message decryption failed: %v", err)
		}

		if decrypted != message {
			t.Error("Decrypted long message does not match original")
		}
	})

	// Test 5: Invalid ciphertext decryption attempt
	t.Run("Invalid Ciphertext", func(t *testing.T) {
		key := make([]byte, 32)
		group := NewSecureGroup(key)

		_, err := group.DecryptMessage("invalid-base64-data")
		if err == nil {
			t.Error("Expected error when decrypting invalid ciphertext but got none")
		}
	})
}

