func TestBasicEcho(t *testing.T) {
	port := "8080"
	go launchServer(port)
	time.Sleep(100 * time.Millisecond) // Give server time to start

	conn, err := net.Dial("tcp", ":"+port)
	if err != nil {
		t.Fatalf("Could not connect to server: %v", err)
	}
	defer conn.Close()

	message := "Hello, server!"
	fmt.Fprintf(conn, "%s\n", message)

	reader := bufio.NewReader(conn)
	response, err := reader.ReadString('\n')
	if err != nil {
		t.Fatalf("Failed to read response: %v", err)
	}

	expected := fmt.Sprintf("Echo: %s\n", message)
	if response != expected {
		t.Errorf("Expected response %q, got %q", expected, response)
	}
}

// Test multiple concurrent connections
func TestConcurrentConnections(t *testing.T) {
	port := "8081"
	go launchServer(port)
	time.Sleep(100 * time.Millisecond)

	for i := 0; i < 3; i++ {
		go func(id int) {
			conn, err := net.Dial("tcp", ":"+port)
			if err != nil {
				t.Errorf("Connection %d failed: %v", id, err)
				return
			}
			defer conn.Close()

			message := fmt.Sprintf("Hello from client %d", id)
			fmt.Fprintf(conn, "%s\n", message)

			reader := bufio.NewReader(conn)
			response, err := reader.ReadString('\n')
			if err != nil {
				t.Errorf("Failed to read response for client %d: %v", id, err)
				return
			}

			expected := fmt.Sprintf("Echo: %s\n", message)
			if response != expected {
				t.Errorf("Client %d: Expected response %q, got %q", id, expected, response)
			}
		}(i)
	}
	time.Sleep(500 * time.Millisecond) // Wait for all connections to complete
}

// Test invalid port number
func TestInvalidPort(t *testing.T) {
	port := "99999" // Invalid port number
	go launchServer(port)
	time.Sleep(100 * time.Millisecond)

