package main

import (
	"fmt"
	"sync"
)

// ChatMessage represents a simple message that could be sent over a channel
type ChatMessage struct {
	Sender    string
	Content   string
}

// sendMessage simulates sending a message to a channel
func sendMessage(wg *sync.WaitGroup, messages chan<- ChatMessage, sender string, content string) {
	defer wg.Done()
	msg := ChatMessage{Sender: sender, Content: content}
	messages <- msg
}

// receiveMessage simulates receiving messages from a channel and printing them
func receiveMessage(wg *sync.WaitGroup, messages <-chan ChatMessage) {
	defer wg.Done()
	for msg := range messages {
		fmt.Printf("%s: %s\n", msg.Sender, msg.Content)
	}
}

func main() {
	var wg sync.WaitGroup
	messageChannel := make(chan ChatMessage, 10)

	// Simulating message sending
	wg.Add(1)
	sendMessage(&wg, messageChannel, "Alice", "Hello Bob!")
	wg.Add(1)
	sendMessage(&wg, messageChannel, "Bob", "Hi Alice, how are you?")

	// Start receiving message
	wg.Add(1)
	go receiveMessage(&wg, messageChannel)

	// Closing the channel after all sends are complete
	wg.Wait()
	close(messageChannel)
}

