package main

import (
	"sync"
	"testing"
	"time"
)

// Test 1: Basic message sending and receiving
func TestBasicMessageFlow(t *testing.T) {
	var wg sync.WaitGroup
	messageChannel := make(chan ChatMessage, 10)

	// Send a message
	wg.Add(1)
	go sendMessage(&wg, messageChannel, "Alice", "Hello World!")

	// Read the message
	msg := <-messageChannel
	if msg.Sender != "Alice" || msg.Content != "Hello World!" {
		t.Errorf("Expected message from Alice saying 'Hello World!', got '%s: %s'", msg.Sender, msg.Content)
	}

	wg.Wait()
	close(messageChannel)
}

// Test 2: Channel buffer capacity
func TestChannelBuffer(t *testing.T) {
	var wg sync.WaitGroup
	messageChannel := make(chan ChatMessage, 2)

	// Send two messages without receiving
	wg.Add(2)
	go sendMessage(&wg, messageChannel, "Alice", "Message 1")
	go sendMessage(&wg, messageChannel, "Bob", "Message 2")

	time.Sleep(100 * time.Millisecond)

	// Verify both messages
	msg1 := <-messageChannel
	msg2 := <-messageChannel

	if msg1.Content != "Message 1" || msg2.Content != "Message 2" {
		t.Error("Messages not received in correct order")
	}

	wg.Wait()
	close(messageChannel)
}

// Test 3: Multiple senders
func TestMultipleSenders(t *testing.T) {
	var wg sync.WaitGroup
	messageChannel := make(chan ChatMessage, 10)

	// Start multiple senders
	senders := []string{"Alice", "Bob", "Charlie"}
	messages := []string{"Hello", "Hi", "Hey"}

	for i := range senders {
		wg.Add(1)
		go sendMessage(&wg, messageChannel, senders[i], messages[i])
	}

	// Collect all messages
	received := make(map[string]bool)
	for i := 0; i < len(senders); i++ {
		msg := <-messageChannel
		received[msg.Content] = true
	}

	// Verify all messages were received
	for _, msg := range messages {
		if !received[msg] {
			t.Errorf("Message '%s' was not received", msg)
		}
	}

	wg.Wait()
	close(messageChannel)
}

// Test 4: Concurrent send and receive
func TestConcurrentSendReceive(t *testing.T) {
	var wg sync.WaitGroup
	messageChannel := make(chan ChatMessage, 10)
	receivedCount := 0

	// Start receiver
	wg.Add(1)
	go func() {
		defer wg.Done()
		for msg := range messageChannel {
			if msg.Sender != "" && msg.Content != "" {
				receivedCount++
			}
		}
	}()

	// Send messages
	messageCount := 5
	for i := 0; i < messageCount; i++ {
		wg.Add(1)
		go sendMessage(&wg, messageChannel, "Tester", "Test message")
	}

	time.Sleep(100 * time.Millisecond)
	close(messageChannel)
