func TestNewWebServer(t *testing.T) {
    addr := ":8080"
    ws := NewWebServer(addr)

    if ws == nil {
        t.Error("NewWebServer returned nil")
    }

    if ws.Address != addr {
        t.Errorf("Expected address %s, got %s", addr, ws.Address)
    }
}

func TestHandleRequest(t *testing.T) {
    ws := NewWebServer(":8080")

    req := httptest.NewRequest(http.MethodGet, "/", nil)
    w := httptest.NewRecorder()

    ws.handleRequest(w, req)

    resp := w.Result()

    if resp.StatusCode != http.StatusOK {
        t.Errorf("Expected status code %d, got %d", http.StatusOK, resp.StatusCode)
    }

    expected := "Welcome to the distributed web-based system using Go!\n"
    if strings.TrimSpace(w.Body.String()) != strings.TrimSpace(expected) {
        t.Errorf("Expected body '%s', got '%s'", expected, w.Body.String())
    }
}

func TestInvalidAddress(t *testing.T) {
    ws := NewWebServer("invalid-address")

    // Create a channel to receive the error
    done := make(chan bool)

    go func() {
        ws.Start()
        done <- true
    }()

    // The server should fail to start with an invalid address
    select {
    case <-done:
        // If we get here, the server started successfully, which shouldn't happen
        t.Error("Server should not start with invalid address")
    default:
        // This is the expected behavior
    }
}

func TestRequestMethod(t *testing.T) {
    ws := NewWebServer(":8080")

    methods := []string{http.MethodGet, http.MethodPost, http.MethodPut, http.MethodDelete}

    for _, method := range methods {
        req := httptest.NewRequest(method, "/", nil)
        w := httptest.NewRecorder()

        ws.handleRequest(w, req)

        resp := w.Result()
        if resp.StatusCode != http.StatusOK {
            t.Errorf("Method %s: Expected status code %d, got %d", 
                    method, http.StatusOK, resp.StatusCode)
        }
    }
}

func TestConcurrentRequests(t *testing.T) {
    ws := NewWebServer(":8080")

    for i := 0; i < 10; i++ {
        go func() {
            req := httptest.NewRequest(http.MethodGet, "/", nil)
            w := httptest.NewRecorder()
            ws.handleRequest(w, req)

            resp := w.Result()
            if resp.StatusCode != http.StatusOK {
                t.Errorf("Expected status code %d, got %d", http.StatusOK, resp.StatusCode)
            }
        }()
    }
}

