    var wg sync.WaitGroup

    wg.Add(3)
    go func() {
        defer wg.Done()
        secureBroadcast("Message 1", key, &mutex)
    }()
    go func() {
        defer wg.Done()
        secureBroadcast("Message 2", key, &mutex)
    }()
    go func() {
        defer wg.Done()
        secureBroadcast("Message 3", key, &mutex)
    }()

    wg.Wait()
}

