package main

import (
	"fmt"
	"sync"
	"time"
)

// MonitorProcess checks the status of distributed processes and applies mutual exclusion to access shared resources.
// It uses the concept of failure detection by routinely pinging processes to ensure they are active.
// The function utilizes Go's goroutines and mutex to handle concurrency and mutual exclusion.

type Process struct {
	ID        int
	isActive  bool
	lastCheck time.Time
}

func MonitorProcess(processes []Process, checkInterval time.Duration, mu *sync.Mutex) {
	for i := range processes {
		go func(idx int) {
			for {
				time.Sleep(checkInterval)
				mu.Lock()
				if time.Since(processes[idx].lastCheck) > 2*checkInterval {
					processes[idx].isActive = false
					fmt.Printf("Process %d is not responding.\n", processes[idx].ID)
				}
				mu.Unlock()
			}
		}(i)
	}
}

// Example usage
func main() {
	var mu sync.Mutex
	processes := []Process{
		{ID: 1, isActive: true, lastCheck: time.Now()},
		{ID: 2, isActive: true, lastCheck: time.Now()},
	}
	go MonitorProcess(processes, 5*time.Second, &mu)

	// Simulating other operations
	time.Sleep(30 * time.Second)
}


