func TestMonitorProcess(t *testing.T) {
	// Test 1: Basic process monitoring
	t.Run("Basic Process Monitoring", func(t *testing.T) {
		var mu sync.Mutex
		processes := []Process{
			{ID: 1, isActive: true, lastCheck: time.Now()},
		}
		go MonitorProcess(processes, 100*time.Millisecond, &mu)
		time.Sleep(300 * time.Millisecond)

		mu.Lock()
		if processes[0].isActive != false {
			t.Error("Process should be marked as inactive after timeout")
		}
		mu.Unlock()
	})

	// Test 2: Multiple processes monitoring
	t.Run("Multiple Processes", func(t *testing.T) {
		var mu sync.Mutex
		processes := []Process{
			{ID: 1, isActive: true, lastCheck: time.Now()},
			{ID: 2, isActive: true, lastCheck: time.Now()},
		}
		go MonitorProcess(processes, 100*time.Millisecond, &mu)
		time.Sleep(300 * time.Millisecond)

		mu.Lock()
		if processes[0].isActive != false || processes[1].isActive != false {
			t.Error("All processes should be marked as inactive after timeout")
		}
		mu.Unlock()
	})

	// Test 3: Process recovery
	t.Run("Process Recovery", func(t *testing.T) {
		var mu sync.Mutex
		processes := []Process{
			{ID: 1, isActive: true, lastCheck: time.Now()},
		}
		go MonitorProcess(processes, 100*time.Millisecond, &mu)

		// Wait for process to be marked inactive
		time.Sleep(300 * time.Millisecond)

		// Update lastCheck to simulate process recovery
		mu.Lock()
		processes[0].lastCheck = time.Now()
		mu.Unlock()

		// Give time for the monitor to detect the recovery
		time.Sleep(100 * time.Millisecond)

		mu.Lock()
		if !processes[0].isActive {
			t.Error("Process should remain active after lastCheck update")
		}
		mu.Unlock()
	})

	// Test 4: Zero processes
	t.Run("Zero Processes", func(t *testing.T) {
		var mu sync.Mutex
		processes := []Process{}
		MonitorProcess(processes, 100*time.Millisecond, &mu)
		// Should not panic or cause any issues
		time.Sleep(200 * time.Millisecond)
	})

	// Test 5: Mutex correctness
	t.Run("Mutex Correctness", func(t *testing.T) {
		var mu sync.Mutex
		processes := []Process{
			{ID: 1, isActive: true, lastCheck: time.Now()},
		}

		// Create a channel to detect deadlock
		done := make(chan bool)

		go MonitorProcess(processes, 100*time.Millisecond, &mu)

		go func() {
			mu.Lock()
			time.Sleep(50 * time.Millisecond)
			mu.Unlock()
			done <- true
		}()

		select {
		case <-done:
			// Test passed - mutex was properly released
		case <-time.After(500 * time.Millisecond):
			t.Error("Possible deadlock detected")
		}
	})
}

