func TestLeaderElection(t *testing.T) {
	// Test 1: Basic election with valid configuration
	t.Run("ValidConfiguration", func(t *testing.T) {
		clientURLs := []string{"localhost:2379"}
		electionName := "/election/test1"

		cfg := clientv3.Config{
			Endpoints:   clientURLs,
			DialTimeout: 5 * time.Second,
		}

		client, err := clientv3.New(cfg)
		if err != nil {
			t.Fatalf("Failed to create etcd client: %v", err)
		}
		defer client.Close()

		sess, err := concurrency.NewSession(client)
		if err != nil {
			t.Fatalf("Failed to create session: %v", err)
		}
		defer sess.Close()

		election := concurrency.NewElection(sess, electionName)

		ctx := context.Background()
		if err := election.Campaign(ctx, "test-leader"); err != nil {
			t.Fatalf("Failed to campaign: %v", err)
		}

		resp, err := election.Leader(ctx)
		if err != nil {
			t.Fatalf("Failed to get leader: %v", err)
		}

		if len(resp.Kvs) == 0 {
			t.Fatal("No leader found")
		}

		if string(resp.Kvs[0].Value) != "test-leader" {
			t.Errorf("Unexpected leader value: got %s, want test-leader", string(resp.Kvs[0].Value))
		}
	})

	// Test 2: Multiple nodes competing for leadership
	t.Run("MultipleNodes", func(t *testing.T) {
		clientURLs := []string{"localhost:2379"}
		electionName := "/election/test2"

		// Create two sessions simulating two nodes
		cfg := clientv3.Config{
			Endpoints:   clientURLs,
			DialTimeout: 5 * time.Second,
		}

		client1, err := clientv3.New(cfg)
		if err != nil {
			t.Fatalf("Failed to create first etcd client: %v", err)
		}
		defer client1.Close()

		client2, err := clientv3.New(cfg)
		if err != nil {
			t.Fatalf("Failed to create second etcd client: %v", err)
		}
		defer client2.Close()

		sess1, err := concurrency.NewSession(client1)
		if err != nil {
			t.Fatalf("Failed to create first session: %v", err)
		}
		defer sess1.Close()

		sess2, err := concurrency.NewSession(client2)
		if err != nil {
			t.Fatalf("Failed to create second session: %v", err)
		}
		defer sess2.Close()

		el1 := concurrency.NewElection(sess1, electionName)
		el2 := concurrency.NewElection(sess2, electionName)

		ctx := context.Background()

		// First node campaigns
		if err := el1.Campaign(ctx, "node1"); err != nil {
			t.Fatalf("Node 1 failed to campaign: %v", err)
		}

		// Second node attempts to campaign
		errChan := make(chan error)
		go func() {
			errChan <- el2.Campaign(ctx, "node2")
		}()

		// Check that node1 is the leader
		resp, err := el1.Leader(ctx)
		if err != nil {
			t.Fatalf("Failed to get leader: %v", err)
		}

		if string(resp.Kvs[0].Value) != "node1" {
			t.Errorf("Unexpected leader value: got %s, want node1", string(resp.Kvs[0].Value))
		}

		// Node1 resigns, Node2 should become leader
		if err := el1.Resign(ctx); err != nil {
			t.Fatalf("Node 1 failed to resign: %v", err)
		}

		select {
		case err := <-errChan:
			if err != nil {
				t.Fatalf("Node 2 campaign failed: %v", err)
			}
		case <-time.After(5 * time.Second):
			t.Fatal("Timeout waiting for node 2 to become leader")
		}

		resp, err = el2.Leader(ctx)
		if err != nil {
			t.Fatalf("Failed to get leader after resignation: %v", err)
		}

		if string(resp.Kvs[0].Value) != "node2" {
			t.Errorf("Unexpected leader value after resignation: got %s, want node2", string(resp.Kvs[0].Value))
		}
	})

	// Test 3: Invalid endpoint configuration
	t.Run("InvalidEndpoint", func(t *testing.T) {
		clientURLs := []string{"invalid-host:2379"}
		electionName := "/election/test3"

		cfg := clientv3.Config{
			Endpoints:   clientURLs,
			DialTimeout: 1 * time.Second,  // Short timeout for faster test
		}

		_, err := clientv3.New(cfg)
		if err == nil {
			t.Fatal("Expected error with invalid endpoint, got nil")
		}
	})

	// Test 4: Session expiration handling
	t.Run("SessionExpiration", func(t *testing.T) {
		clientURLs := []string{"localhost:2379"}
		electionName := "/election/test4"

		cfg := clientv3.Config{
			Endpoints:   clientURLs,
			DialTimeout: 5 * time.Second,
		}

		client, err := clientv3.New(cfg)
		if err != nil {
			t.Fatalf("Failed to create etcd client: %v", err)
		}
		defer client.Close()

		// Create session with very short TTL
		sess, err := concurrency.NewSession(client, concurrency.WithTTL(1))
		if err != nil {
			t.Fatalf("Failed to create session: %v", err)
		}

		election := concurrency.NewElection(sess, electionName)
		ctx := context.Background()

		if err := election.Campaign(ctx, "test-leader"); err != nil {
			t.Fatalf("Failed to campaign: %v", err)
		}

		// Close session to simulate expiration
		sess.Close()

		// Wait for TTL to expire
		time.Sleep(2 * time.Second)

		// Try to get leader - should fail or return no leader
		resp, _ := election.Leader(ctx)
		if resp != nil && len(resp.Kvs) > 0 {
			t.Error("Expected no leader after session expiration")
		}
	})

	// Test 5: Context cancellation
	t.Run("ContextCancellation", func(t *testing.T) {
		clientURLs := []string{"localhost:2379"}
		electionName := "/election/test5"

		cfg := clientv3.Config{
			Endpoints:   clientURLs,
			DialTimeout: 5 * time.Second,
		}

		client, err := clientv3.New(cfg)
		if err != nil {
			t.Fatalf("Failed to create etcd client: %v", err)
		}
		defer client.Close()

		sess, err := concurrency.NewSession(client)
		if err != nil {
			t.Fatalf("Failed to create session: %v", err)
		}
		defer sess.Close()

		election := concurrency.NewElection(sess, electionName)

		ctx, cancel := context.WithCancel(context.Background())

		// Cancel context before campaign
		cancel()

		err = election.Campaign(ctx, "test-leader")
		if err == nil {
			t.Error("Expected error due to cancelled context")
		}
	})
}

